    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        if(tuser.getText().equals("")&&tpass.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Username Dan Password Kosong");
        }
        else if(tuser.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Username Kosong");
        }
        else if(tpass.getText().equals("")){
            JOptionPane.showMessageDialog(null, "Password Kosong");
        }
        try{
            sql = "select * from pegawai where username='"+tuser.getText()+"' and password='"+tpass.getText()+"'";
            rs = stat.executeQuery(sql);
            if(rs.next()){
                if(tuser.getText().equals(rs.getString("username"))&&tpass.getText().equals(rs.getString("password"))){
                    JOptionPane.showMessageDialog(null,"Login Sukses");
                    new Menu().setVisible(true);
                    this.dispose();
                }
            }else{
                JOptionPane.showMessageDialog(null,"Username or Password Salah!");
            }
        }catch (Exception e){
            JOptionPane.showMessageDialog(null,"Login Gagal Turn ON XAMPP"+e);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

