    private static class koneksi {
            Connection con;
            Statement stm;            
            
            public void config(){
            try{
                Class.forName("com.mysql.jdbc.Driver");
                con = DriverManager.getConnection("jdbc:mysql://localhost:3306/visualperpustakaan","root","");
                stm = con.createStatement();
            }catch(Exception e){
                JOptionPane.showMessageDialog(null, "koneksi gagal Turn ON XAMPP "+e.getMessage());
            }
        }        
    }    
