    private void koneksibuku(){
      try {
          Class.forName("com.mysql.jdbc.Driver");
          con=DriverManager.getConnection("jdbc:mysql://localhost:3306/VisualPerpustakaan","root","");
          //System.out.println("Berhasil Koneksi");
          //JOptionPane.showMessageDialog(null, "Koneksi Berhasil");
        }catch (Exception e) {
          System.out.println("Gagal Koneksi Turn on XAMPP"+e);
        }
    }
    
    private void tampilbuku(){
      Object[] Baris={"Kode Buku","Judul Buku","Pengarang","Penerbit","Jumlah","Kode Rak"};
      grid =new DefaultTableModel(null, Baris);
      tablebuku.setModel(grid);
      sql= "select * from buku ";
      try {
          java.sql.Statement stat = con.createStatement();
          ResultSet hasil= stat.executeQuery(sql);
            while (hasil.next()){
                String a = hasil.getString("kd_buku");
                String b = hasil.getString("judul");
                String c = hasil.getString("pengarang");
                String d = hasil.getString("penerbit");
                String e = hasil.getString("jumlah");
                String f = hasil.getString("kd_rak");
                
                String[] data={a,b,c,d,e,f};
                grid.addRow(data);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }
    
    private void pilihrak(){
        cmbrak.removeAllItems();
        cmbrak.addItem("Pilih");
        try {
           sql ="select * from rak";
           st=con.createStatement();
           rs=st.executeQuery(sql);
            while (rs.next()) { 
                String rak =rs.getString("kd_rak");
                cmbrak.addItem(rak);
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null,"Gagal Menampilkan Kode Buku \n" +e.getMessage());
        }
    }
    
    private void kosongkan(){
        tkd_buku.setText("");
        tjd_buku.setText("");
        tpeng_buku.setText("");
        tpen_buku.setText("");
        jml_buku.setText("");
        tkd_buku.requestFocus();
    }
    
    

