    private void bupdate_bukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdate_bukuActionPerformed
        // TODO add your handling code here:
        sql="update buku set judul=?, pengarang=?,penerbit=?,jumlah=? where kd_buku=?";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            st.setString(1,tjd_buku.getText());
            st.setString(2,tpeng_buku.getText());
            st.setString(3,tpen_buku.getText());
            st.setString(4,jml_buku.getText());
            st.setString(5,tkd_buku.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diupdate");
            kosongkan();
            tkd_buku.requestFocus();
            tampilbuku();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di Update");
        }
    }//GEN-LAST:event_bupdate_bukuActionPerformed

    private void bsrc_bukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsrc_bukuActionPerformed
        // TODO add your handling code here:
        Object[] Baris = {"Kode Buku","Judul Buku","Pengarang","Sumber","Tahun Terbit","Jumlah Buku"};
        grid =new DefaultTableModel(null, Baris);
        tablebuku.setModel(grid);
        sql= "select * from buku where judul like '%"+tsrc_buku.getText()+"%' ";
        try {
            java.sql.Statement stat = con.createStatement();
            ResultSet hasil= stat.executeQuery(sql);
              while (hasil.next()){
                String a = hasil.getString("kd_buku");
                String b = hasil.getString("judul");
                String c = hasil.getString("pengarang");
                String d = hasil.getString("penerbit");
                String e = hasil.getString("jumlah");
                
                String[] data={a,b,c,d,e};
                grid.addRow(data);
             }           
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }//GEN-LAST:event_bsrc_bukuActionPerformed

    private void bdel_bukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdel_bukuActionPerformed
        // TODO add your handling code here:
        int ok=JOptionPane.showConfirmDialog(null,"Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0) {
            try {
                sql="delete from buku where kd_buku='"+ tkd_buku.getText() +"'";
                PreparedStatement st=con.prepareStatement(sql);
                st.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                kosongkan();
                tkd_buku.requestFocus();
                tampilbuku();
            } catch (Exception e) {
                JOptionPane.showConfirmDialog(null, "Tidak Ada Data Yang Di Hapus ....!! \n"
                    +e.getMessage());
            }
        }
    }//GEN-LAST:event_bdel_bukuActionPerformed

    private void bsave_bukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsave_bukuActionPerformed
        // TODO add your handling code here:
        sql= "insert into buku values (?,?,?,?,?,?)";
        try{
            PreparedStatement st=con.prepareStatement(sql);
            st.setString(1,tkd_buku.getText());
            st.setString(2,tjd_buku.getText());
            st.setString(3,tpeng_buku.getText());
            st.setString(4,tpen_buku.getText());
            st.setString(5,jml_buku.getText());
            st.setString(6,cmbrak.getItemAt(cmbrak.getSelectedIndex()).toString());
            
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosongkan();
            tkd_buku.requestFocus();
            tampilbuku();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_bsave_bukuActionPerformed

