    private void tablebukuMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablebukuMouseClicked
        // TODO add your handling code here:
        int bar = tablebuku.getSelectedRow();
        String a = grid.getValueAt(bar, 0).toString();
        String b = grid.getValueAt(bar, 1).toString();
        String c = grid.getValueAt(bar, 2).toString();
        String d = grid.getValueAt(bar, 3).toString();
        String e = grid.getValueAt(bar, 4).toString();
        String f = grid.getValueAt(bar, 5).toString();

        tkd_buku.setText(a);
        tjd_buku.setText(b);
        tpeng_buku.setText(c);
        tpen_buku.setText(d);
        jml_buku.setText(e);
        cmbrak.setSelectedItem(f);
    }//GEN-LAST:event_tablebukuMouseClicked

