    private void tampilpegawai(){
        Object[] Baris={"Kode Pegawai","NIP","Nama","Tempat","Tanggal Lahir","Jenis Kelamin","Alamat"
        ,"Jadwal","Username","Password"};
        grid =new DefaultTableModel(null, Baris);
        tablepegawai.setModel(grid);
        String sql= "select * from pegawai ";
        try {
            java.sql.Statement stat = con.createStatement();
            ResultSet hasil= stat.executeQuery(sql);
            while (hasil.next()){
                String a = hasil.getString("kd_pegawai");
                String b = hasil.getString("nip");
                String c = hasil.getString("nama");
                String d = hasil.getString("tempat");                
                String e = hasil.getString("tgl_lahir");
                String f = hasil.getString("jk");
                String g = hasil.getString("alamat");
                String h = hasil.getString("jadwal");
                String i = hasil.getString("username");
                
                String[] data={a,b,c,d,e,f,g,h,i};
                grid.addRow(data);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }
    
