    private void bsave_pegawaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsave_pegawaiActionPerformed
        // TODO add your handling code here:
        String sql= "insert into pegawai values (?,?,?,?,?,?,?,?,?,?)";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            st.setString(1,kd_pegawai.getText());
            st.setString(2,tnip_pegawai.getText());
            st.setString(3,tnama_pegawai.getText());
            st.setString(4,ttmpt_pegawai.getText());
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            st.setString(5,tanggal =format.format(dpegawai.getDate()));
            String jkel="";
            if(rjk1_pegawai.isSelected()) jkel="Laki-Laki";
            else jkel="Perempuan";
            st.setString(6,jkel);
            st.setString(7,talmt_pegawai.getText());
            st.setString(8,cjadwal_pegawai.getSelectedItem().toString());
            st.setString(9,username.getText());
            st.setString(10,password.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Di Simpan");
            kosongkan();
            kd_pegawai.requestFocus();
            tampilpegawai();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+ e);
        }
    }//GEN-LAST:event_bsave_pegawaiActionPerformed

    private void tablepegawaiMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablepegawaiMouseClicked
        // TODO add your handling code here:
        int bar = tablepegawai.getSelectedRow();
        String a = grid.getValueAt(bar, 0).toString();
        String b = grid.getValueAt(bar, 1).toString();
        String c = grid.getValueAt(bar, 2).toString();
        String d = grid.getValueAt(bar, 3).toString();
        String e = grid.getValueAt(bar, 4).toString();
        String f = grid.getValueAt(bar, 5).toString();
        String g = grid.getValueAt(bar, 6).toString();
        String h = grid.getValueAt(bar, 7).toString();
        String i = grid.getValueAt(bar, 8).toString();
        
        kd_pegawai.setText(a);
        tnip_pegawai.setText(b);
        tnama_pegawai.setText(c);
        ttmpt_pegawai.setText(d);
        try{            
            java.util.Date date = new SimpleDateFormat("yyyy-MM-dd").parse(e);
            dpegawai.setDate(date); 
        }catch(Exception m){}
        if(f.equals("Laki-Laki")){rjk1_pegawai.setSelected(true); rjk2_pegawai.setSelected(false);}
        else {rjk1_pegawai.setSelected(false); rjk2_pegawai.setSelected(true);}
        talmt_pegawai.setText(g);
        cjadwal_pegawai.setSelectedItem(h);
        username.setText(i);
    }//GEN-LAST:event_tablepegawaiMouseClicked

    private void UpdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_UpdateActionPerformed
        // TODO add your handling code here:
         String sql="update pegawai set nip=?, nama=?,tempat=?,tgl_lahir=?, jk=?,"
                + "alamat=?, jadwal=?,username=?,password=? where kd_pegawai=?";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            st.setString(1,tnip_pegawai.getText());
            st.setString(2,tnama_pegawai.getText());
            st.setString(3,ttmpt_pegawai.getText());
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            st.setString(4,tanggal =format.format(dpegawai.getDate()));
            String jkel="";
            if(rjk1_pegawai.isSelected()) jkel="Laki-Laki";
            else jkel="Perempuan";
            st.setString(5,jkel);
            st.setString(6,talmt_pegawai.getText());
            st.setString(7,cjadwal_pegawai.getSelectedItem().toString());
            st.setString(8,username.getText());
            st.setString(9,password.getText());
            st.setString(10, kd_pegawai.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diupdate");
            kosongkan();
            kd_pegawai.requestFocus();
            tampilpegawai();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di Update"+e);
        }
    }//GEN-LAST:event_UpdateActionPerformed

