    private void bdelete_pegawaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdelete_pegawaiActionPerformed
        // TODO add your handling code here:
        int ok=JOptionPane.showConfirmDialog(null,"Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0) {
            try {
                sql="delete from pegawai where kd_pegawai='"+ kd_pegawai.getText() +"'";
                PreparedStatement st=con.prepareStatement(sql);
                st.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                kosongkan();
                kd_pegawai.requestFocus();
                tampilpegawai();
            } catch (Exception e) {
                JOptionPane.showConfirmDialog(null, "Tidak Ada Data Yang Di Hapus ....!! \n"
                    +e.getMessage());
            }
        }
    }//GEN-LAST:event_bdelete_pegawaiActionPerformed

