    private void koneksiPeminjaman(){
      try {
          Class.forName("com.mysql.jdbc.Driver");
          con=DriverManager.getConnection("jdbc:mysql://localhost:3306/visualperpustakaan","root","");
          //System.out.println("Koneksi Berhasil !!!");
         // JOptionPane.showMessageDialog(null,"Koneksi Berhasil");
        } catch (Exception e) {
          System.out.println("Gagal Koneksi Check XAMPP"+e);
        }
    }
    
    private void tampilPeminjaman(){
        Object[] Baris={"No Peminjam","Tanggal Pinjam","Kode Anggota","Kode Buku","Jatuh Tempo","Total Pinjam "};
        grid =new DefaultTableModel(null, Baris);
        tabelpinjam.setModel(grid);
        String sql= "select * from peminjaman ";
        try {
            java.sql.Statement stat = con.createStatement();
            ResultSet hasil= stat.executeQuery(sql);
              while (hasil.next()){
                  String a = hasil.getString("kd_pinjam");
                  String b = hasil.getString("tgl_pinjam");
                  String c = hasil.getString("kd_anggota");
                  String d = hasil.getString("kd_buku");
                  String e = hasil.getString("tempo");
                  String f = hasil.getString("jumlah");

                  String[] data={a,b,c,d,e,f};
                  grid.addRow(data);
              }
        } catch (Exception e) {
              JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }
    
    public void waktu(){
        java.util.Date tgl=new java.util.Date();
        dpinjam.setDate(tgl);
        dtempo.setDate(tgl);
    }
    
