    private void bsaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsaveActionPerformed
        // TODO add your handling code here:
        String sql= "insert into peminjaman values (?,?,?,?,?,?)";
        try{
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");            
            PreparedStatement ps=con.prepareStatement(sql);
            ps.setString(1,tkd_pinjam.getText());
            ps.setString(2,tanggal =format.format(dpinjam.getDate()));
            ps.setString(3,tkd_angpinjam.getText());
            ps.setString(4,tkd_buku.getText());
            ps.setString(5,tanggal =format.format(dtempo.getDate()));
            ps.setString(6,txttot_pinjam.getText());
            
            ps.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosongkan();
            tkd_pinjam.requestFocus();
            tampilPeminjaman();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_bsaveActionPerformed

    private void bcariActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcariActionPerformed
        // TODO add your handling code here:
        Object[] Baris = {"Kode Pinjam","Tanggal Pinjam","Kode Anggota","Kode Buku","Jatuh Tempo","Total Pinjam "};
        grid =new DefaultTableModel(null, Baris);
        tabelpinjam.setModel(grid);
        String sql= "select * from peminjaman where kd_pinjam like '%"+tcarikode.getText()+"%' ";
        try {
            java.sql.Statement stat = con.createStatement();
            ResultSet hasil= stat.executeQuery(sql);
              while (hasil.next()){
                String a = hasil.getString("kd_pinjam");
                  String b = hasil.getString("tgl_pinjam");
                  String c = hasil.getString("kd_anggota");
                  String d = hasil.getString("kd_buku");
                  String e = hasil.getString("tempo");
                  String f = hasil.getString("jumlah");
                
                String[] data={a,b,c,d,e,f};
                grid.addRow(data);
             }           
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }//GEN-LAST:event_bcariActionPerformed

    private void bcariagtActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcariagtActionPerformed
        // TODO add your handling code here:
         try {
            String sql="select * from anggota where kd_anggota='"+ tkd_angpinjam.getText()+"'";
            st=con.createStatement();
            rsanggota =st.executeQuery(sql);
            while (rsanggota.next()) {
                String na = rsanggota.getString("nama");
                tnm_pinjam.setText(na);
                tnm_pinjam.setEnabled(false);
                String alamat = rsanggota.getString("alamat");
                talamat_pinjam.setText(alamat);
                talamat_pinjam.setEnabled(false);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_bcariagtActionPerformed

    private void bcaribkActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcaribkActionPerformed
        // TODO add your handling code here:
        try {
            String sql="select * from buku where kd_buku='"+ tkd_buku.getText()+"'";
            st=con.createStatement();
            rsbuku =st.executeQuery(sql);
            while (rsbuku.next()) {
                String jb = rsbuku.getString("judul");
                tjudulbuku.setText(jb);
                tjudulbuku.setEnabled(false);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_bcaribkActionPerformed

