    private void tabelpinjamMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tabelpinjamMouseClicked
        // TODO add your handling code here:
        int bar = tabelpinjam.getSelectedRow();
        String a = grid.getValueAt(bar, 0).toString();
        String b = grid.getValueAt(bar, 1).toString();
        String c = grid.getValueAt(bar, 2).toString();
        String d = grid.getValueAt(bar, 3).toString();
        String e = grid.getValueAt(bar, 4).toString();
        String f = grid.getValueAt(bar, 5).toString();
        tkd_pinjam.setText(a);
        try{            
            java.util.Date tgl = new SimpleDateFormat("yyyy-MM-dd").parse(b);
            dpinjam.setDate(tgl); 
        }catch(Exception m){}
        tkd_angpinjam.setText(c);
        tkd_buku.setText(d);
        try{            
            java.util.Date tgl = new SimpleDateFormat("yyyy-MM-dd").parse(e);
            dtempo.setDate(tgl); 
        }catch(Exception m){}
        txttot_pinjam.setText(f);
    }//GEN-LAST:event_tabelpinjamMouseClicked

    private void bupdateActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdateActionPerformed
        // TODO add your handling code here:
        String sql="update peminjaman set tgl_pinjam=?, kd_anggota=?, kd_buku=?, tempo=?, jumlah=? where kd_pinjam=?";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            st.setString(1,tanggal =format.format(dpinjam.getDate()));
            st.setString(2,tkd_angpinjam.getText());
            st.setString(3,tkd_buku.getText());
            st.setString(4,tanggal =format.format(dtempo.getDate()));            
            st.setString(5,txttot_pinjam.getText());
            st.setString(6,tkd_pinjam.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diupdate");
            kosongkan();
            tkd_pinjam.requestFocus();
            tampilPeminjaman();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di Update"+e);
        }
    }//GEN-LAST:event_bupdateActionPerformed

    private void bdeleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdeleteActionPerformed
        // TODO add your handling code here:
        int ok=JOptionPane.showConfirmDialog(null,"Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_OPTION);
        if (ok==0) {
            try {
                String sql="delete from peminjaman where kd_pinjam='"+ tkd_pinjam.getText() +"'";
                PreparedStatement st=con.prepareStatement(sql);
                st.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                kosongkan();
                tkd_pinjam.requestFocus();
                tampilPeminjaman();
            } catch (Exception e) {
                JOptionPane.showConfirmDialog(null, "Tidak Ada Data Yang Di Hapus ....!! \n"
                    +e.getMessage());
            }
        }
    }//GEN-LAST:event_bdeleteActionPerformed

