    private void koneksiuser(){
      try {
          Class.forName("com.mysql.jdbc.Driver");
          con=DriverManager.getConnection("jdbc:mysql://localhost:3306/visualperpustakaan","root","");
          //System.out.println("Berhasil Konek");
          //JOptionPane.showMessageDialog(null, "Koneksi Berhasil");
        } catch (Exception e) {
          System.out.println("Gagal Koneksi Turn ON XAMPP"+e);
        }
    }
    
    protected void datatable(){
        Object[] Baris={"No Pengadaan", "Tanggal", "Kode Buku", "Jumlah", "Id Pegawai"};
        tabmode = new DefaultTableModel(null,Baris);
        tablepengadaan.setModel(tabmode);
        String sql="select * from pengadaan";
        try {
            java.sql.Statement stat = con.createStatement();
            ResultSet hasil= stat.executeQuery(sql);
            while (hasil.next()){
                String a = hasil.getString("nopengadaan");
                String b = hasil.getString("tanggal");
                String c = hasil.getString("kd_buku");
                String d = hasil.getString("jumlah");                
                String e = hasil.getString("kd_pegawai");                
                
                String[] data={a,b,c,d,e};
                tabmode.addRow(data);
            }
        } catch (Exception e) {
            JOptionPane.showMessageDialog(null , "Gagal Tampil " +e);
        }
    }
    
    private void pilihpegawai(){
        cid_pegawai.removeAllItems();
        cid_pegawai.addItem("Pilih");
        try {
           String sql ="select * from pegawai";
           Statement st=con.createStatement();
           rs=st.executeQuery(sql);
            while (rs.next()) { 
                String pegawai =rs.getString("kd_pegawai");
                cid_pegawai.addItem(pegawai);
            }
        } catch (SQLException e) {
            JOptionPane.showMessageDialog(null,"Gagal Menampilkan Kode Pegawai \n" +e.getMessage());
        }
    }
    
    private void kosongkan(){
        tnp.setText("");
        tkd_buku.setText("");
        tjudulbuku.setText("");
        tjml_pengadaan.setText("");
        cid_pegawai.setSelectedItem(null);
        tnamakaryawan.setText("");
        tnp.requestFocus();
    }
    
    public void waktu(){
        java.util.Date tgl=new java.util.Date();
        date.setDate(tgl);
    }
