    private void datePropertyChange(java.beans.PropertyChangeEvent evt) {//GEN-FIRST:event_datePropertyChange
        // TODO add your handling code here:
        if (date.getDate()!=null) {
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            tanggal =format.format(date.getDate());
        }
    }//GEN-LAST:event_datePropertyChange

    private void bsave_pengadaanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsave_pengadaanActionPerformed
        // TODO add your handling code here:
        String sql= "insert into pengadaan values (?,?,?,?,?)";
        try{
            PreparedStatement st=con.prepareStatement(sql);
            st.setString(1,tnp.getText());
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            st.setString(2,tanggal =format.format(date.getDate()));
            st.setString(3,tkd_buku.getText());
            st.setString(4,tjml_pengadaan.getText());
            st.setString(5,cid_pegawai.getSelectedItem().toString());
            
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosongkan();
            tnp.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan"+e);
        }
    }//GEN-LAST:event_bsave_pengadaanActionPerformed

    private void bupdate_pengadaanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdate_pengadaanActionPerformed
        // TODO add your handling code here:
        String sql="update pengadaan set tanggal=?, kd_buku=?,jumlah=?,kd_pegawai=? where nopengadaan=?";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd");
            st.setString(1,tanggal =format.format(date.getDate()));
            st.setString(2,tkd_buku.getText());
            st.setString(3,tjml_pengadaan.getText());
            st.setString(4,cid_pegawai.getSelectedItem().toString());
            st.setString(5,tnp.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diupdate");
            kosongkan();
            tnp.requestFocus();
            datatable();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di Update");
        }
    }//GEN-LAST:event_bupdate_pengadaanActionPerformed

    private void bdel_pengadaanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bdel_pengadaanActionPerformed
        // TODO add your handling code here:
        int ok=JOptionPane.showConfirmDialog(null,"Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_OPTION);
        if (ok==0) {
            try {
                String sql="delete from pengadaan where nopengadaan='"+ tnp.getText() +"'";
                PreparedStatement st=con.prepareStatement(sql);
                st.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                kosongkan();
                tnp.requestFocus();
                datatable();
            } catch (Exception e) {
                JOptionPane.showConfirmDialog(null, "Tidak Ada Data Yang Di Hapus ....!! \n"
                    +e.getMessage());
            }
        }
    }//GEN-LAST:event_bdel_pengadaanActionPerformed

