    private void tablepengadaanMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablepengadaanMouseClicked
        // TODO add your handling code here:
        int bar = tablepengadaan.getSelectedRow();
        String a = tabmode.getValueAt(bar, 0).toString();
        String b = tabmode.getValueAt(bar, 1).toString();
        String c = tabmode.getValueAt(bar, 2).toString();
        String d = tabmode.getValueAt(bar, 3).toString();
        String e = tabmode.getValueAt(bar, 4).toString();

        tnp.setText(a);
        try{            
            java.util.Date tgl = new SimpleDateFormat("yyyy-MM-dd").parse(b);
            date.setDate(tgl); 
        }catch(Exception m){}
        tkd_buku.setText(c);
        tjml_pengadaan.setText(d);
        cid_pegawai.setSelectedItem(e);
    }//GEN-LAST:event_tablepengadaanMouseClicked

    private void bcaribukuActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bcaribukuActionPerformed
        // TODO add your handling code here:
        try {
            String sql="select * from buku where kd_buku='"+ tkd_buku.getText()+"'";
            st=con.createStatement();
            rs =st.executeQuery(sql);
            while (rs.next()) {
                String jb = rs.getString("judul");
                tjudulbuku.setText(jb);
                tjudulbuku.setEnabled(false);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_bcaribukuActionPerformed

    private void cid_pegawaiItemStateChanged(java.awt.event.ItemEvent evt) {//GEN-FIRST:event_cid_pegawaiItemStateChanged
        // TODO add your handling code here:
        try {
            String sql="select * from pegawai where kd_pegawai='"+ cid_pegawai.getSelectedItem()+"'";
            st=con.createStatement();
            rs=st.executeQuery(sql);
            while (rs.next()) {
                tnamakaryawan.setText(rs.getString(3));
                tnamakaryawan.setEnabled(false);
            }
        } catch (Exception e) {
        }
    }//GEN-LAST:event_cid_pegawaiItemStateChanged

