    private void bsave_rakActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bsave_rakActionPerformed
        // TODO add your handling code here:
        String sql= "insert into rak values (?,?)";
        try{
            PreparedStatement st=con.prepareStatement(sql);
            st.setString(1,kd_rak.getText());
            st.setString(2,tinput_rak.getText());
            
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Disimpan");
            kosongkan();
            tinput_rak.requestFocus();
            tampilrak();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data Gagal Disimpan");
        }
    }//GEN-LAST:event_bsave_rakActionPerformed

    private void tablerakMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tablerakMouseClicked
        // TODO add your handling code here:
        int bar = tablerak.getSelectedRow();
        String a = grid.getValueAt(bar, 0).toString();
        String b = grid.getValueAt(bar, 1).toString();
        
        kd_rak.setText(a);
        tinput_rak.setText(b);
    }//GEN-LAST:event_tablerakMouseClicked

    private void bupdate_rakActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bupdate_rakActionPerformed
        // TODO add your handling code here:
        String sql="update rak set lokasi=?, where kd_rak=?";
        try{
            PreparedStatement st= con.prepareStatement(sql);
            st.setString(1,tinput_rak.getText());
            st.setString(2, kd_rak.getText());
            st.executeUpdate();
            JOptionPane.showMessageDialog(null, "Data Berhasil Diupdate");
            kosongkan();
            kd_rak.requestFocus();
            tampilrak();
        }catch(SQLException e){
            JOptionPane.showMessageDialog(null, "Data gagal di Update");
        }kosongkan();
    }//GEN-LAST:event_bupdate_rakActionPerformed

    private void deleteActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_deleteActionPerformed
        // TODO add your handling code here:
        int ok=JOptionPane.showConfirmDialog(null,"Hapus","Konfirmasi Dialog",JOptionPane.YES_NO_CANCEL_OPTION);
        if (ok==0) {
            try {
                sql="delete from rak where kd_rak='"+ kd_rak.getText() +"'";
                PreparedStatement st=con.prepareStatement(sql);
                st.executeUpdate();
                JOptionPane.showMessageDialog(null,"Data Berhasil Di Hapus");
                //kosongkan();
                kd_rak.requestFocus();
                //tampilrak();
            } catch (Exception e) {
                JOptionPane.showConfirmDialog(null, "Tidak Ada Data Yang Di Hapus ....!! \n"
                    +e.getMessage());
            }tampilrak();
        }kosongkan();
    }//GEN-LAST:event_deleteActionPerformed

